/** Print to log automatically while including the netmode. */
	UFUNCTION(BlueprintCallable, Category = "Helper", meta = (WorldContext = "WorldContextObject", CallableWithoutWorldContext, DisplayName = "Log"))
	static void K2_Log(UObject* WorldContextObject, const FString& String);

	/** Log a warning to the output log while including the netmode. */
	UFUNCTION(BlueprintCallable, Category = "Helper", meta = (WorldContext = "WorldContextObject", CallableWithoutWorldContext, DisplayName = "Log Warning"))
	static void K2_LogWarning(UObject* WorldContextObject, const FString& String);

	/** Log an error to the output log while including the netmode. */
	UFUNCTION(BlueprintCallable, Category = "Helper", meta = (WorldContext = "WorldContextObject", CallableWithoutWorldContext, DisplayName = "Log Error"))
	static void K2_LogError(UObject* WorldContextObject, const FString& String);

	/** Log a fatal error to the output log crashing the game while including the netmode. */
	UFUNCTION(BlueprintCallable, Category = "Helper", meta = (WorldContext = "WorldContextObject", CallableWithoutWorldContext, DisplayName = "Log Fatal"))
	static void K2_LogFatal(UObject* WorldContextObject, const FString& String);

{
	UEnum* EnumPtr = FindObject<UEnum>((UObject*)ANY_PACKAGE, *EnumString, true);
	if (EnumPtr)
	{
		int32 EnumIndex = EnumPtr->GetIndexByValue(EnumValue);
		return (*EnumPtr->GetDisplayNameTextByIndex(EnumIndex).ToString());
	}

	return "";
}

/** @return the ENetRole of InActor as a string - actor is null checked */
	static FString GetNetRole(AActor* InActor);

	/** @return the ENetRole of InActor as a string - actor is null checked */
	static FString GetNetRole(const AActor* InActor);

	/**
	* @return the DisplayName of the enum (passed as EnumString) based on the EnumValue
	* eg. EnumToString("EMyEnum", (uint8)EnumValue);
	* @note : an empty string result implies the enum lookup failed (and you should double check the EnumString is correct)
	*/
	static FString EnumToString(FString EnumString, uint8 EnumValue);
